mapper.update:
   	UPDATE board

		BOARD_CONTENT = #{boardContent},
		BOARD_TITLE = #{boardTitle}

   	WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT * FROM ( SELECT inner_table. * , ROWNUM AS row_seq FROM ( SELECT BOARD_NO, NICKNAME, TRAV_NO, HITS, BOARD_CONTENT, BOARD_TITLE, REG_DATE, BOARD_CATEGORY, DELETE_FLAG FROM board WHERE BOARD_CATEGORY = #{boardCategory}
AND BOARD_NO LIKE '%'||#{searchKeyword}||'%'
AND BOARD_TITLE LIKE '%'||#{searchKeyword}||'%'
AND NICKNAME LIKE '%'||#{searchKeyword}||'%'
ORDER BY REG_DATE DESC) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * FROM ( SELECT inner_table. * , ROWNUM AS row_seq FROM ( SELECT BOARD_NO, NICKNAME, TRAV_NO, HITS, BOARD_CONTENT, BOARD_TITLE, REG_DATE, BOARD_CATEGORY, DELETE_FLAG FROM board WHERE BOARD_CATEGORY = #{boardCategory}
AND BOARD_NO LIKE '%'||#{searchKeyword}||'%'
AND BOARD_TITLE LIKE '%'||#{searchKeyword}||'%'
AND NICKNAME LIKE '%'||#{searchKeyword}||'%'
ORDER BY REG_DATE ASC) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*) FROM (SELECT BOARD_NO, NICKNAME, TRAV_NO, HITS, BOARD_CONTENT, BOARD_TITLE, REG_DATE, BOARD_CATEGORY, DELETE_FLAG FROM board WHERE BOARD_CATEGORY = #{boardCategory}
AND BOARD_NO LIKE '%'||#{searchKeyword}||'%'
AND BOARD_TITLE LIKE '%'||#{searchKeyword}||'%'
AND NICKNAME LIKE '%'||#{searchKeyword}||'%'
) countTable

SELECT BOARD_NO, NICKNAME, TRAV_NO, HITS, BOARD_CONTENT, BOARD_TITLE, REG_DATE, BOARD_CATEGORY
FROM board
WHERE BOARD_NO = #{boardNo}
