mapper.update:
UPDATE follow 
SET is_following = #{isFollowing} 
WHERE fol_user_id = #{folUserId} 
AND target_user_id = #{targetUserId}

mapper.select:
SELECT COUNT(*)
FROM ( SELECT follow_no, fol_user_id, target_user_id 
				FROM follow
				WHERE fol_user_id = #{searchKeyword}
			 ) countTable

SELECT COUNT(*)
FROM ( SELECT follow_no, fol_user_id, target_user_id 
				FROM follow
				WHERE target_user_id = #{searchKeyword}
			 ) countTable
